## init

# general
HDG="Thermal Data Log"
COL_HDGS=(Day YYYY/MM/DD TZ WLAN\(째C\) SSD\(째C\) CPU_pkg\(째C\) CPU_cores\(째C\) Fan\(RPM\) LocalTime)
DATECMD="'date' +%a\ %Y/%m/%d\ %Z"
DEFAULT_DIR=$HOME/useful_bash_stuff/bin
DEFAULT_THERMFILE=$DEFAULT_DIR/thermal.txt
DEFAULT_LOGFILE=$DEFAULT_DIR/thermal.log
if [ -d $DEFAULT_DIR ]; then
  THERMFILE=$DEFAULT_THERMFILE
  LOGFILE=$DEFAULT_LOGFILE
else
  THERMFILE=$HOME/heat_mon.txt
  LOGFILE=$HOME/heat_mon.log
fi

# check for THERMFILE's existence
if [ ! -e $THERMFILE ]; then
  echo "Error: Thermal datafile from heat_mon.sh not found. Exiting." 1>&2
  exit 2
fi


## MAIN

# get entry to record
ENTRY=$('tail' -1 $THERMFILE)
# generate line
LOGLINE="$(eval $DATECMD) $ENTRY"

# add LOGLINE to LOGFILE, creating LOGFILE if needed
if [ -e $LOGFILE ]; then
  LOGLINE_FMT=$(echo -e ${COL_HDGS[*]} "@" $LOGLINE)
  echo $LOGLINE_FMT | tr '@' '\n' | 'column' -t | 'tail' -1 >> $LOGFILE
else
  echo $HDG > $LOGFILE
  echo -e ${COL_HDGS[*]} "\n" $LOGLINE | 'column' -t >> $LOGFILE
fi


# all pau!   )
exit 0
